/* eslint-disable */
<template>

  <div class="app">
    <form @submit.prevent>
      <h4>Создание поста</h4>
      <input v-bind:value="title"
             @input="inputTitle"
             class="input"
             type="text"
             placeholder="Название">
      <input v-bind:value="body"
             @input="inputBody"
             class="input"
             type="text"
             placeholder="описание">
      <button class="btn" @click="createPost">Создать</button>
    </form>
    <div>

    </div>
  </div>

</template>

<script>
export default {
  data() {
    return {
      posts: [
        {id: 1, title: 'JS', body: 'описание'},
        {id: 2, title: 'JS1', body: 'описание1'},
        {id: 3, title: 'JS2', body: 'описание2'},
      ],
      title: '',
      body: '',
    }
  },
  methods: {
    createPost() {
      const newPost = {
        id: Date.now(),
        title: this.title,
        body: this.body,
      }
      this.posts.push(newPost);
      this.body ="";
      this.title =""
    },
    inputTitle(event){
      console.log(event)
      this.title = event.target.value
    },
    inputBody(event){
      this.body = event.target.value
    }

  }

}
</script>

<style scoped>
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

.post {
  margin-top: 15px;
  padding: 15px;
  border: 2px solid teal;
}

form {
  display: flex;
  flex-direction: column;
}

.input {
  width: 100%;
  border: 1px solid teal;
  padding: 10px 15px;
  margin-top: 15px;
}

.btn {
  margin-top: 15px;
  align-self: flex-end;
  padding: 10px 15px;
  background: none;
  color: teal;
  border: 1px solid teal;
}

</style>
