/* eslint-disable */
<template>
  <h1>Страница с постами</h1>
  <my-button
  @click="showDialog"
  >Создать пост</my-button>
  <my-dialog v-model:show="dialogVisible">
    <post-form @create="createPost"/>
  </my-dialog>
  <div class="app">
    <post-list
        :posts="posts"
        @remove="removePost"
    />
  </div>

</template>

<script>
import postList from "@/сomponents/PostList";
import postForm from "@/сomponents/PostForm";

export default { // в скрипте по дефолту экспортируется объект
  components: {
    postList, postForm
  },
  data() {   //поле
    return {
      posts: [//модель
        {id: 1, title: 'JS', body: 'описание'},
        {id: 2, title: 'JS1', body: 'описание1'},
        {id: 3, title: 'JS2', body: 'описание2'},
      ],
      dialogVisible:false,
    }
  },
  methods: { //поле
    createPost(post) { //брал экземпляр из текущей модели
      this.posts.push(post)
    },
    removePost(post) {
      this.posts = this.posts.filter(p => p.id !== post.id)
    },
    showDialog(){
      this.dialogVisible=true;
    }
  }
}
</script>

<style scoped>
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}


</style>
