<template>

  <div class="app">
    <post-form/>
    <post-list :posts="posts" />

  </div>

</template>

<script>
import postList from "@/сomponents/PostList";
import postForm from "@/сomponents/PostForm";

export default {
  components: {
    postList, postForm
  },
  data() {
    return {
      title: '',
      body: '',
    }
  },
  methods: {
    createPost() {
      const newPost = {
        id: Date.now(),
        title: this.title,
        body: this.body,
      }
      this.posts.push(newPost);
      this.body = "";
      this.title = ""
    },
    inputTitle(event) {
      console.log(event)
      this.title = event.target.value
    },
    inputBody(event) {
      this.body = event.target.value
    }

  }

}
</script>

<style scoped>
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}


</style>
